import React from 'react';

function Courses() {
  const courseList = [
    { 
      name: 'Full Stack Development', 
      info: 'Learn HTML, CSS, JavaScript, React, Node.js, Express, MongoDB, and deployment.' 
    },
    { 
      name: 'Java Programming', 
      info: 'Master Core Java, Advanced Java, Spring Boot, Hibernate, and REST APIs.' 
    },
    { 
      name: 'Data Science', 
      info: 'Cover Python, Data Analysis, Machine Learning, Deep Learning, and AI tools.' 
    }
  ];

  return (
    <section>
      <h2>Courses Available</h2>
      {courseList.map((course, index) => (
        <details key={index} className="course-box">
          <summary className="course-header">{course.name}</summary>
          <div className="course-details">{course.info}</div>
        </details>
      ))}
    </section>
  );
}

export default Courses;
