import React, { useState } from 'react';

function Courses() {
  const courseList = [
    { 
      name: 'Full Stack Development', 
      info: 'Learn HTML, CSS, JavaScript, React, Node.js, Express, MongoDB, and deployment.' 
    },
    { 
      name: 'Java Programming', 
      info: 'Master Core Java, Advanced Java, Spring Boot, Hibernate, and REST APIs.' 
    },
    { 
      name: 'Data Science', 
      info: 'Cover Python, Data Analysis, Machine Learning, Deep Learning, and AI tools.' 
    }
  ];

  const [openIndex, setOpenIndex] = useState(null);

  const toggleCourse = (index) => {
    setOpenIndex(openIndex === index ? null : index);
  };

  return (
    <section>
      <h2>Courses Available</h2>
      {courseList.map((course, index) => (
        <div 
          key={index} 
          className="course-box"
        >
          <div 
            className="course-header"
            onClick={() => toggleCourse(index)}
          >
            {course.name}
          </div>
          {openIndex === index && (
            <div className="course-details">
              {course.info}
            </div>
          )}
        </div>
      ))}
    </section>
  );
}

export default Courses;
