import React, { useState } from 'react';

function Courses() {
  const courseList = [
    { 
      name: 'Full Stack Development', 
      info: 'Covers HTML, CSS, JavaScript, React, Node.js, Express, and MongoDB.' 
    },
    { 
      name: 'Java Programming', 
      info: 'Covers Core Java, Advanced Java, Spring Boot, Hibernate, and REST APIs.' 
    },
    { 
      name: 'Data Science', 
      info: 'Covers Python, Machine Learning, Data Analysis, and AI concepts.' 
    }
  ];

  const [openIndex, setOpenIndex] = useState(null);

  const toggleCourse = (index) => {
    setOpenIndex(openIndex === index ? null : index);
  };

  return (
    <section style={{ padding: '20px' }}>
      <h2>Courses Available</h2>
      {courseList.map((course, index) => (
        <div key={index} style={{ marginBottom: '10px', border: '1px solid #ccc', borderRadius: '5px' }}>
          <div 
            style={{ padding: '10px', cursor: 'pointer', backgroundColor: '#f4f4f4' }}
            onClick={() => toggleCourse(index)}
          >
            {course.name}
          </div>
          {openIndex === index && (
            <div style={{ padding: '10px', backgroundColor: '#fff' }}>
              {course.info}
            </div>
          )}
        </div>
      ))}
    </section>
  );
}

export default Courses;
