import React from "react";

export default class ItemForm extends React.Component {
  constructor() {
    super();
    this.state = {
      item: "Books",
      quantity: ""
    };
  }

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  render() {
    const { item, quantity } = this.state;
    const prices = { Books: 70, Pencil: 5, Pen: 10 };

    
    const totalCost = quantity * prices[item];

    return (
      <div style={{ border: "1px solid black", padding: "20px", width: "300px" }}>
        <h4 style={{ color: "#68cf48" }}>Item Purchase Form</h4>

        <label>Item: </label>
        <select name="item" value={item} onChange={this.handleChange}>
          <option value="Books">Books</option>
          <option value="Pencil">Pencil</option>
          <option value="Pen">Pen</option>
        </select>
        <br /><br />

        <label>Quantity: </label>
        <input
          type="number"
          name="quantity"
          placeholder="Enter quantity"
          value={quantity}
          onChange={this.handleChange}
        />
        <br /><br />

        <label>Total Cost: </label>
        <input type="text" value={totalCost} readOnly />
      </div>
    );
  }
}
