import React, { Component } from "react";

export default class StudentControlledForm extends Component
{
    constructor(props)
    {
        super(props);
        this.state={name:'',qualification:'',result:''}
        
    }

    handleSubmit=(e)=>{
        let s= "Your name is : '+this.state.name+'\nQualification : '+this.state.Qualification'"
        this.setState({result:"Name - "+this.state.name+""+" Qualification - "})
        e.preventDefault();
    }

    handleChangeName=(e)=>{
        const name = e.target.name;
        this.setState({name:e.target.value})
    };

    handleQualification=(e)=>{
        const qual = e.target.value;
        this.setState({qualification:qual})
    };

    render()
    {
        return(
            <>
                <form onSubmit={this.handleSubmit}>
                    <fieldset>
                        
                    
                <input
                  type="text"
                  id="txtname"
                  name="custname"
                  placeholder="Full Name"
                  autoComplete="off"
                  onChange={this.handleChangeName}
                  className="form-control"
                />

                <input
                  type="text"
                  id="txtqual"
                  name="qual"
                  placeholder="Qualification"
                  autoComplete="off"
                  onChange={this.handleQualification}
                  className="form-control"
                />
                <button>Submit</button>
                </fieldset>
                </form>

                <p>{this.state.result && <b>{this.state.result}</b>}</p>

            </>
        )
    }

}
