import React from "react";

export default class ItemForm extends React.Component {
  constructor() {
    super();
    this.state = {
      item: "Books",
      quantity: "",
      cost: ""
    };
  }

  handleChange = (event) => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  };

  handleReset = () => {
    this.setState({
      item: "Books",
      quantity: "",
      cost: ""
    });
  };

  render() {
    // auto-calculate total
    const totalCost = (parseInt(this.state.quantity) || 0) * (parseFloat(this.state.cost) || 0);

    return (
      <div style={{ border: "1px solid black", padding: "20px", width: "300px" }}>
        <h4 style={{ color: "#68cf48" }}>Item Purchase Form</h4>

        <label>Item: </label>
        <select name="item" value={this.state.item} onChange={this.handleChange}>
          <option value="Books">Books</option>
          <option value="Pencil">Pencil</option>
          <option value="Pen">Pen</option>
        </select>
        <br /><br />

        <label>Quantity: </label>
        <input
          type="number"
          name="quantity"
          placeholder="Enter quantity"
          value={this.state.quantity}
          onChange={this.handleChange}
        />
        <br /><br />

        <label>Cost: </label>
        <input
          type="number"
          name="cost"
          placeholder="Enter cost"
          value={this.state.cost}
          onChange={this.handleChange}
        />
        <br /><br />

        <button onClick={this.handleReset}>Reset</button>
        <br /><br />

        <label>Total Cost: </label>
        <input type="text" value={totalCost} readOnly />
      </div>
    );
  }
}
