import react from "react";

export default class DemoEventSecond extends react.Component
{
    constructor()
    {
        super();
        this.state = {
            subject:""
        }
    }
    handleChange = (event) => 
    {
         this.setState({ subject: event.target.value})
    }

    render()
    {
        return
        (       <div>
                    <h4 style={{colour : "#68cf48"}}>State Handling</h4>
                    <lable>Enter your Favoraite Programming Language</lable>
                    <input placeholder="Enter here" onChange={this.handleChange} />
                    <p><b>{this.state.subjet}</b>is your Favorite program</p>
                </div>
            
        )
    }
}
