import React, { Component } from 'react';

class ItemCalculator extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedItem: 'book',
      quantity: '',
      cost: '',
      totalCost: null
    };
  }

  handleItemChange = (event) => {
    this.setState({ selectedItem: event.target.value });
  }

  handleQuantityChange = (event) => {
    this.setState({ quantity: event.target.value });
  }

  handleCostChange = (event) => {
    this.setState({ cost: event.target.value });
  }

  handleSubmit = (event) => {
    event.preventDefault();
    const { quantity, cost } = this.state;
    const total = parseFloat(quantity) * parseFloat(cost);
    this.setState({ totalCost: isNaN(total) ? null : total });
  }

  render() {
    return (
      <div style={{ padding: '20px', maxWidth: '300px' }}>
        <h3>Item Cost Calculator</h3>
        <form onSubmit={this.handleSubmit}>
          <div>
            <label>Item: </label>
            <select value={this.state.selectedItem} onChange={this.handleItemChange}>
              <option value="book">Book</option>
              <option value="pen">Pen</option>
              <option value="notebook">Notebook</option>
            </select>
          </div>
          <div>
            <label>Quantity: </label>
            <input
              type="number"
              value={this.state.quantity}
              onChange={this.handleQuantityChange}
              required
            />
          </div>
          <div>
            <label>Cost per item: </label>
            <input
              type="number"
              value={this.state.cost}
              onChange={this.handleCostChange}
              required
            />
          </div>
          <button type="submit">Submit</button>
        </form>

        {this.state.totalCost !== null && (
          <div style={{ marginTop: '20px' }}>
            <strong>Total Cost: â‚¹{this.state.totalCost.toFixed(2)}</strong>
          </div>
        )}
      </div>
    );
  }
}

export default ItemCalculator;
