import React from 'react';

export default class Form extends React.Component
{
    constructor()
    {
        super();
        this.state = {
            Name:"",
            Email:"",
            Message:""
        }
    }
    handleChange = (event) => 
    {
        this.setState({ Name: event.target.value})
        this.setState({ Email: event.target.value})
        this.setState({ Message: event.target.value})
    }

    render()
    {
        return(    
               <div>
                    <h4 style={{colour : "#68cf48"}}>State Handling 2</h4>
                    <label>Enter your Name</label>
                    <input placeholder="Enter Name" onChange={this.handleChange} />

                    <label>Enter your Email : </label>
                    <input placeholder=" Enter Email" onChange={this.handleChange} />

                    <label>Enter your Message : </label>
                    <input placeholder=" Enter Message " onChange={this.handleChange} />

                    <p><b>{this.state.Name}</b> is your Name</p>
                </div>
            
        )
    }
}
