import React from "react";

export default class ItemForm extends React.Component {
  constructor() {
    super();
    this.state = {
      item: "Books",
      quantity: "",
      cost: "",
      totalCost: 0
    };
  }

  handleChange = (event) => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  };

  calculateTotal = () => {
    const { quantity, cost } = this.state;
    const total = (parseInt(quantity) || 0) * (parseFloat(cost) || 0);
    this.setState({ totalCost: total });
  };

  handleReset = () => {
    this.setState({
      item: "Books",
      quantity: "",      
      cost: "",
      totalCost: 0
    });
  };

  render() {
    return (
      <div style={{ border: "1px solid black", padding: "20px", width: "300px" }}>
        <h4 style={{ color: "#68cf48" }}>Item Purchase Form</h4>

        <label>Item: </label>
        <select name="item" value={this.state.item} onChange={this.handleChange}>
          <option value="Books">Books</option>
          <option value="Pencil">Pencil</option>
          <option value="Pen">Pen</option>
        </select>
        <br /><br />

        <label>Quantity: </label>
        <input
          type="number"
          name="quantity"
          placeholder="Enter quantity"
          value={this.state.quantity}
          onChange={this.handleChange}
        />
        <br /><br />

        <label>Cost: </label>
        <input
          type="number"
          name="cost"
          placeholder="Enter cost"
          value={this.state.cost}
          onChange={this.handleChange}
        />
        <br /><br />

        <button onClick={this.calculateTotal}>Submit</button>
        <button onClick={this.handleReset} style={{ marginLeft: "10px" }}>
          Reset
        </button>
        <br /><br />

        <label>Total Cost: </label>
        <input type="text" value={this.state.totalCost} readOnly />
      </div>
    );
  }
}
